import React from 'react';
import PropTypes from "prop-types";
import { drizzleConnect } from "drizzle-react";
import ContractForm from "../ContractForm";
import ContractData from "../ContractData";

class DepositWithdraw extends React.Component {

	constructor(props, context) {
		super(props);
		this.contracts = context.drizzle.contracts;
		this.state = {
			dataKey: this.contracts["MadridCentral"].methods["getBalance"].cacheCall({from: this.props.accounts[0]}),
			inputValue: 0
		}
		console.log(this);
	}

	render () {

		if (!(this.state.dataKey in this.props.MadridCentral.getBalance)) {
	      return <span>Fetching...</span>;
	    }

	    var balance = this.props.MadridCentral["getBalance"][this.state.dataKey];
	    console.log(balance);
	    return(
	    	<div>
	    		<input type="number" onChange={evt => this.updateInputValue(evt)} />
				<ContractForm contract="MadridCentral" name="Ingresar" method="deposit" sendArgs={{from: this.props.accounts[0], value: this.inputValue}} />
				<ContractData contract="MadridCentral" method="getBalance"/>
				<ContractForm contract="MadridCentral" name="Retirar fondos" method="withdraw" sendArgs={{from: this.props.accounts[0]}} />
			</div>
	    );

	}	

	updateInputValue(evt) {
	    this.setState({
	      inputValue: evt.target.value
	    });
  	}
}

DepositWithdraw.contextTypes = {
  drizzle: PropTypes.object,
};

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

export default drizzleConnect(DepositWithdraw, mapStateToProps);
