import React from 'react';
import PropTypes from 'prop-types';
import { drizzleConnect } from "drizzle-react";
import { Card, InputGroup, Form, Row, Col, Button, Alert} from "react-bootstrap";

class Invitation extends React.Component {
	constructor(props, context) {
		super(props);
		this.drizzle = context.drizzle;
		this.state = {
			day: 1,
			month: 1,
			year: 2019,
			timestamp: 0,
			isDateSelected: false,
			dataKey: this.contracts["MadridCentral"].methods["getInvitationFromDay"].cacheCall()
		}
	}

	render() {

		if (!(this.state.dataKey in this.props.MadridCentral.getInvitationFromDay)) {
	      return <span>Fetching...</span>;
	    }

	    var invitation = this.props.MadridCentral["getInvitationFromDay"][this.state.dataKey].value;


		return(
			<div>
				<br></br>
				<Card as="div" style={{ width: '30rem'}}>
					Tu invitation para el {this.props.day}/{this.props.month}/{this.props.year} es:
					<Row>
                <Col>
                  <div className="buttonCenter">
                    <Alert variant="dark">
                      <Alert.Heading>Hola</Alert.Heading>
                    </Alert>
                  </div>
                </Col>
              </Row>
				</Card>
				<Invitation 
					isDateSelected={this.state.isDateSelected}
					year={this.state.year}
					month={this.state.month}
					day={this.state.day}
					timestamp={this.state.timestamp} 
				/>
			</div>
		);
	}

  	monthToString(month) {
  		switch (month) {
			case 1:
			    return "January";
			break;
			case 2:
			    return "February";
			break;
			case 3:
			    return "March";
			break;
			case 4:
			    return "April";
			break;
			case 5:
			    return "May";
			break;
			case 6:
			    return "June";
			break;
			case 7:
			    return "July";
			break;
			case 8:
			    return "August";
			break;
			case 9:
			    return "September";
			break;
			case 10:
			    return "October";
			break;
			case 11:
			    return "November";
			break;
			case 12:
			    return "December";
			break;
			default: 
				return "";
			break;

		}
  	}
}

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

Invitation.contextTypes = {
    drizzle: PropTypes.object
};

export default drizzleConnect(Invitation, mapStateToProps);
