import React from 'react';
import PropTypes from "prop-types";
import { drizzleConnect } from "drizzle-react";

class DepositWithdraw extends React.Component {

	constructor(props, context) {
		super(props);
		this.contracts = context.drizzle.contracts;
		this.state = {
			dataKey: this.contracts["MadridCentral"].methods["getBalance"].cacheCall({from: state.accounts[0]}),
		}
		console.log(this);
	}

	render () {

		if (!(this.state.dataKey in this.props.MadridCentral.getBalance)) {
	      return <span>Fetching...</span>;
	    }

	    var balance = this.props.MadridCentral["getBalance"][this.state.dataKey];
	    console.log(balance);
	    return(
	    	<div>
				<button>Ingresar</button>
				<input type="text" />
				<button>Retirar fondos</button>
				<h1>{balance.value}</h1>
			</div>
	    );

	}	
}

DepositWithdraw.contextTypes = {
  drizzle: PropTypes.object,
};

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
  };
};

export default drizzleConnect(DepositWithdraw, mapStateToProps);
