import React from 'react';
import ContractForm from "../ContractForm";
import ContractData from "../ContractData";
import PropTypes from 'prop-types';
import ActualDay from './ActualDay';
import { drizzleConnect } from "drizzle-react";

class Guest extends React.Component {

	constructor(props, context) {
		super(props);
		this.contracts = context.drizzle.contracts;
		this.state = {
      		dataKey: this.contracts["MadridCentral"].methods["getTodayAddress"].cacheCall()
    	};
	}

	render() {

		if (!(this.state.dataKey2 in this.props.MadridCentral.getTodayAddress)) {
	      return <span>Fetching...</span>;
	    }

		var actualDay = this.props.MadridCentral["getTodayAddress"][this.state.dataKey].value;

		console.log(actualDay);

		return (
			<div>
				<ContractForm contract="MadridCentral" name="Pedir invitacion" method="getInvitation" />
				<ContractData contract="MadridCentral" method="currentDay"/>
				<ActualDay direccion={actualDay} />
			</div>
		);
	}
}

Guest.contextTypes = {
  drizzle: PropTypes.object,
};

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

export default drizzleConnect(Guest, mapStateToProps);
