import React from 'react';
import PropTypes from 'prop-types';
import { drizzleConnect } from "drizzle-react";
import { Card, InputGroup, Form, FormControl, Row, Col, Button} from "react-bootstrap";

class Edit extends React.Component {
	constructor(props, context) {
		super(props);
		this.drizzle = context.drizzle;
		this.state = {
			day: 0,
			month: 0,
			year: 0
		}
	}

	render() {
		console.log(this.state);
		return(
			<div>
				<br></br>
				<Card as="div" style={{ width: '50rem'}}>
					<Row>
						<Col>
							<label>Día</label>
							<InputGroup className="mb-3" onChange={evt => this.updateDay(evt)}>
		    					<Form.Control as="select" type="number">
							    	<option>1</option>
							    	<option>2</option>
							    	<option>3</option>
							    	<option>4</option>
							    	<option>5</option>
							    	<option>6</option>
							    	<option>7</option>
							    	<option>8</option>
							    	<option>9</option>
							    	<option>10</option>
							    	<option>11</option>
							    	<option>12</option>
							    	<option>13</option>
							    	<option>14</option>
							    	<option>15</option>
							    	<option>16</option>
							    	<option>17</option>
							    	<option>18</option>
							    	<option>19</option>
							    	<option>20</option>
							    	<option>21</option>
							    	<option>22</option>
							    	<option>23</option>
							    	<option>24</option>
							    	<option>25</option>
							    	<option>26</option>
							    	<option>27</option>
							    	<option>28</option>
							    	<option>29</option>
							    	<option>30</option>
							    	<option>31</option>
							    </Form.Control>
	  						</InputGroup>
						</Col>
						<Col>
							<label>Mes</label>
							<InputGroup className="mb-3" onChange={evt => this.updateMonth(evt)}>
		    					<Form.Control as="select" type="number">
							    	<option value="1">Enero</option>
							    	<option value="2">Febrero</option>
							    	<option value="3">Marzo</option>
							    	<option value="4">Abril</option>
							    	<option value="5">Mayo</option>
							    	<option value="6">Junio</option>
							    	<option value="7">Julio</option>
							    	<option value="8">Agosto</option>
							    	<option value="9">Septiembre</option>
							    	<option value="10">Octubre</option>
							    	<option value="11">Noviembre</option>
							    	<option value="12">Diciembre</option>
							    </Form.Control>
	  						</InputGroup>
						</Col>
						<Col>
							<label>Año</label>
							<InputGroup className="mb-3" onChange={evt => this.updateYear(evt)}>
		    					<Form.Control as="select" type="number">
							    	<option>2019</option>
							    	<option>2018</option>
							    </Form.Control>
	  						</InputGroup>
						</Col>
					</Row>

	  				<Row>
	  					<Col>
	  						<div className="buttonCenter">
		  						<Button variant="dark" size="lg" onClick={() => this.onClick()}>
									Editar datos
								</Button>
							</div>
	  					</Col>
	  				</Row>
				</Card>
			</div>
		);
	}

	updateDay(evt) {
	    this.setState({
	      day: parseInt(evt.target.value)
	    });
  	}

  	updateMonth(evt) {
	    this.setState({
	      month: evt.target.value
	    });
  	}

  	updateYear(evt) {
	    this.setState({
	      year: evt.target.value
	    });
  	}

  	onClick() {
	  this.drizzle.contracts.MadridCentral.methods.changeResidentData.cacheSend(this.state.name, this.state.email, this.state.phone, this.state.price, this.state.invitations, {from: this.props.accounts[0]})
  	}
}

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

Edit.contextTypes = {
    drizzle: PropTypes.object
};

export default drizzleConnect(Edit, mapStateToProps);
