import React from 'react';
import PropTypes from 'prop-types';
import { drizzleConnect } from "drizzle-react";
import { Card, InputGroup, Form, Row, Col, Button} from "react-bootstrap";

class Edit extends React.Component {
	constructor(props, context) {
		super(props);
		this.drizzle = context.drizzle;
		this.state = {
			day: 1,
			month: 1,
			year: 2019,
			dataKey: ""
		}
	}

	render() {
		if (!(this.state.dataKey in this.props.MadridCentral.getInvitationFromDay)) {
	      return <span>Fetching...</span>;
	    }

	    var invitation = this.props.MadridCentral["getInvitationFromDay"][this.state.dataKey].value;

		return(
			<div>
				<br></br>
				<Card as="div" style={{ width: '30rem'}}>
					<Row>
						<Col>
							<label>Día</label>
							<InputGroup className="mb-3" onChange={evt => this.updateDay(evt)}>
		    					<Form.Control as="select" type="number">
							    	<option>1</option>
							    	<option>2</option>
							    	<option>3</option>
							    	<option>4</option>
							    	<option>5</option>
							    	<option>6</option>
							    	<option>7</option>
							    	<option>8</option>
							    	<option>9</option>
							    	<option>10</option>
							    	<option>11</option>
							    	<option>12</option>
							    	<option>13</option>
							    	<option>14</option>
							    	<option>15</option>
							    	<option>16</option>
							    	<option>17</option>
							    	<option>18</option>
							    	<option>19</option>
							    	<option>20</option>
							    	<option>21</option>
							    	<option>22</option>
							    	<option>23</option>
							    	<option>24</option>
							    	<option>25</option>
							    	<option>26</option>
							    	<option>27</option>
							    	<option>28</option>
							    	<option>29</option>
							    	<option>30</option>
							    	<option>31</option>
							    </Form.Control>
	  						</InputGroup>
						</Col>
						<Col>
							<label>Mes</label>
							<InputGroup className="mb-3" onChange={evt => this.updateMonth(evt)}>
		    					<Form.Control as="select" type="number">
							    	<option value="1">Enero</option>
							    	<option value="2">Febrero</option>
							    	<option value="3">Marzo</option>
							    	<option value="4">Abril</option>
							    	<option value="5">Mayo</option>
							    	<option value="6">Junio</option>
							    	<option value="7">Julio</option>
							    	<option value="8">Agosto</option>
							    	<option value="9">Septiembre</option>
							    	<option value="10">Octubre</option>
							    	<option value="11">Noviembre</option>
							    	<option value="12">Diciembre</option>
							    </Form.Control>
	  						</InputGroup>
						</Col>
						<Col>
							<label>Año</label>
							<InputGroup className="mb-3" onChange={evt => this.updateYear(evt)}>
		    					<Form.Control as="select" type="number">
							    	<option>2019</option>
							    	<option>2018</option>
							    </Form.Control>
	  						</InputGroup>
						</Col>
					</Row>

	  				<Row>
	  					<Col>
	  						<div className="buttonCenter">
		  						<Button variant="dark" size="lg" onClick={() => this.onClick()}>
									Ver invitación
								</Button>
							</div>
	  					</Col>
	  				</Row>
				</Card>
				<Card as="div" style={{ width: '30rem'}}>
					{invitation}
				</Card>
			</div>
		);
	}

	updateDay(evt) {
	    this.setState({
	      day: parseInt(evt.target.value)
	    });
  	}

  	updateMonth(evt) {
	    this.setState({
	      month: parseInt(evt.target.value)
	    });
  	}

  	updateYear(evt) {
	    this.setState({
	      year: parseInt(evt.target.value)
	    });
  	}

  	monthToString(month) {
  		switch (month) {
			case 1:
			    return "January";
			break;
			case 2:
			    return "February";
			break;
			case 3:
			    return "March";
			break;
			case 4:
			    return "April";
			break;
			case 5:
			    return "May";
			break;
			case 6:
			    return "June";
			break;
			case 7:
			    return "July";
			break;
			case 8:
			    return "August";
			break;
			case 9:
			    return "September";
			break;
			case 10:
			    return "October";
			break;
			case 11:
			    return "November";
			break;
			case 12:
			    return "December";
			break;
			default: 
				return "";
			break;

		}
  	}

  	onClick() {
  		var timestamp = date.getTime() / (1000 * 60 * 60 * 24);
  		console.log(timestamp);
  		this.setState({
	      dataKey: this.drizzle.contracts["MadridCentral"].methods["getInvitationFromDay"].cacheCall(timestamp),
	    });
  	}
}

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

Edit.contextTypes = {
    drizzle: PropTypes.object
};

export default drizzleConnect(Edit, mapStateToProps);
