			break;
			case 7:
			    return "July";
			break;
			case 8:
			    return "August";
			break;
			case 9:
			    return "September";
			break;
			case 10:
			    return "October";
			break;
			case 11:
			    return "November";
			break;
			case 12:
			    return "December";
			break;
			default: 
				return "";
			break;

		}
  	}*/
}

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

Invitation.contextTypes = {
    drizzle: PropTypes.object
};

export default drizzleConnect(Invitation, mapStateToProps);
