import React from 'react';
import { drizzleConnect } from "drizzle-react";
import ContractForm from "../ContractForm";
import ContractData from "../ContractData";
import { Card, InputGroup, FormControl, Row, Col, Button } from "react-bootstrap";

class DepositWithdraw extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			inputValue: 0
		}
	}

	render () {
	    return(
	    	<Row>
	    		<Col>
					<Card style={{ width: '30rem'}}>
						<label>Ingresar</label>
						<InputGroup className="mb-3" onChange={evt => this.updateName(evt)}>
		    				<FormControl
		      					placeholder="Introduce tu matrícula"
		      					aria-label="Introduce tu matrícula"
		      					aria-describedby="basic-addon1"
		    				/>
	  					</InputGroup>
					</Card>
				</Col>
				<Col>
					<Card style={{ width: '30rem'}}>
						<label>Matrícula</label>
						<InputGroup className="mb-3" onChange={evt => this.updateName(evt)}>
		    				<FormControl
		      					placeholder="Introduce tu matrícula"
		      					aria-label="Introduce tu matrícula"
		      					aria-describedby="basic-addon1"
		    				/>
	  					</InputGroup>
					</Card>
				</Col>
			</Row>
	    );

	}	

	updateInputValue(evt) {
	    this.setState({
	      inputValue: evt.target.value
	    });
  	}
}



const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

export default drizzleConnect(DepositWithdraw, mapStateToProps);
