import React from 'react';
import { Redirect, withRouter } from 'react-router-dom';
import { drizzleConnect } from "drizzle-react";
import ContractData from "../ContractData";
import ContractForm from "../ContractForm";


class Resident extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			inputValue: 0
		}
	}

	render () {

		var exisistingResident = <ContractData contract="MadridCentral" method="residentExists"/>
		console.log(exisistingResident);

		return (
				<div>
					{exisistingResident}
					<ContractForm contract="MadridCentral" name="addResident" method="addResident" sendArgs={{from: this.props.accounts[0]}} />

				</div>

			);


		if (exisistingResident) {
			return (
				<div>
					<h1>Hola</h1>
				</div>

			);
		} else {
			return (
				<div>
					<Redirect to="/newResident" />

				</div>

			);
		}
	}
}

const mapStateToProps = state => {
  return {
    MadridCentral: state.contracts.MadridCentral,
    accounts: state.accounts
  };
};

export default drizzleConnect(Resident, mapStateToProps);
